script:
const threshold = 50; // ค่า threshold ที่คุณต้องการ
const value = 60; // ค่าที่จะนำมาเทียบกับ threshold

const svgRect = document.getElementById('myRect').querySelector('rect');

if (value >= threshold) {
  svgRect.setAttribute('fill', '#FF0000'); // สีแดงถ้าค่าเกิน threshold
} else {
  svgRect.setAttribute('fill', '#00FF00'); // สีเขียวถ้าค่าน้อยกว่า threshold
}
